if (!function_exists('routeName')) {
    function routeName($name): string
    {
        try {
            $name = explode('.', str_replace('index', 'list', $name));
            unset($name[0]);
            return implode(' ', $name);
        } catch (Exception $exception) {
            return $exception->getMessage();
        }
    }
}

if (!function_exists('clearCache')) {
    function clearCache()
    {
        try {
            Artisan::call('cache:clear');
            Artisan::call('view:clear');
            Artisan::call('config:clear');
            Artisan::call('event:clear');
            Artisan::call('route:clear');
            Artisan::call('optimize:clear');
            file_put_contents(storage_path('logs/laravel.log'), '');
            file_put_contents(storage_path('logs/cron_jobs.log'), '');
            exec('composer dump-autoload');
            return 'Cleared..!';
        } catch (Exception $exception) {
            return $exception->getMessage();
        }
    }
}

if (!function_exists('sendSmsPattern')) {
    function sendSmsPattern($pattern, $mobile, array $message)
    {
        try {
            $client = new SoapClient("http://ippanel.com/class/sms/wsdlservice/server.php?wsdl");
            return $client->sendPatternSms('3000505', $mobile, 'mehrpol', '%gq~2~ZZ806y', $pattern, $message);
        } catch (Exception $exception) {
            return $exception->getMessage();
        }
    }
}

if (!function_exists('checkMobileNumber')) {
    function checkMobileNumber($mobile, $length = 11)
    {
        try {
            if (strlen($mobile) == $length and is_numeric($mobile) and preg_match("/^09[0-9]{9}$/", $mobile)) return true;
            return false;
        } catch (Exception $exception) {
            return $exception->getMessage();
        }
    }
}

if (!function_exists('nestedTranslate')) {
    function nestedTranslate($value)
    {
        if (is_array($value)) {
            return array_map('nestedTranslate', $value);
        }
        return __($value);
    }
}

if (!function_exists('readingTime')) {
    function readingTime($content, int $wpm = 230)
    {
        $minutes = floor((int)str_word_count($content) / $wpm);
        return (int)$minutes < 1 ? 1 : (int)$minutes . ' ' . __('minutes reading');
    }
}

if (!function_exists('isDomainAvailable')) {
    function isDomainAvailable($domain)
    {
        if (!filter_var($domain, FILTER_VALIDATE_URL)) return false;
        $curlInit = curl_init($domain);
        curl_setopt($curlInit, CURLOPT_CONNECTTIMEOUT, 10);
        curl_setopt($curlInit, CURLOPT_HEADER, true);
        curl_setopt($curlInit, CURLOPT_NOBODY, true);
        curl_setopt($curlInit, CURLOPT_RETURNTRANSFER, true);
        $response = curl_exec($curlInit);
        curl_close($curlInit);
        if ($response) return true;
        return false;
    }
}

if (!function_exists('makeHash')) {
    function makeHash($value): string
    {
        $hash = Hash::make($value);
        return Str::replace('/', '', $hash);
    }
}

if (!function_exists('bytesToHuman')) {
    function bytesToHuman($bytes): string
    {
        $units = ['B', 'KB', 'MB', 'GB', 'TB', 'PB'];
        for ($i = 0; $bytes > 1024; $i++) {
            $bytes /= 1024;
        }
        return round($bytes, 2) . ' ' . $units[$i];
    }
}